import WithDva from './store';
export const WithComponent = (Components: {
    (props: any): JSX.Element;
    getInitialProps?: (props: any) => Promise<{
        [key: string]: any;
        pathname: any;
        query: any;
        isServer: any;
        dvaStore: any;
    }>
}, initData?: () => { [key: string]: any }) => {
    Components.getInitialProps = async (props) => {
        const {
            pathname, query, isServer, store,
        } = props;
        // await props.store.dispatch({ type: 'index/init' });
        return {
            pathname, query, isServer, dvaStore: store, ...initData()
        };
    };
    return WithDva((state) => ({ index: state.index }))(Components)
}
