import React from 'react';
import { Input } from 'antd';
const Home = (prop: { userAgent: string }) => {

    return <>这里是user <Input />{prop.userAgent} </>
}
Home.prototype.getInitialProps = async ({ req }) => {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent }
}
export default Home;
